// Helper functions

func intToStr(val, defaultVal int) string {
	if val == 0 {
		return fmt.Sprint(defaultVal)
	}
	return fmt.Sprint(val)
}

func boolToStr(b bool) string {
	if b {
		return "true"
	}
	return "false"
}

func floatToStr(f float32) string {
	return fmt.Sprintf("%.2f", f)
}

